    private void confirmQuit() {
        int reponse = JOptionPane.showConfirmDialog(null, "Voulez vous vraiment quitter", "Confirmation",
                JOptionPane.YES_NO_OPTION, JOptionPane.PLAIN_MESSAGE);

        if (reponse == JOptionPane.YES_OPTION) {
            dispose();
        }
    }

